import React from 'react';
// import { Carousel } from 'react-carousel3';
import '../components/FrontPageStyle.css';

import Image1 from '../assets/1.jpeg';
import Image2 from '../assets/2.jpeg';
import Image3 from '../assets/3.jpeg';
import Image4 from '../assets/4.jpeg';
import Image5 from '../assets/5.jpeg';
import Image6 from '../assets/6.jpeg';
import Image7 from '../assets/7.jpeg';

const Images = [Image1, Image3, Image4, Image5, Image7];

import { Carousel } from 'react-responsive-carousel';

export default function FrontPage() {
  const [audio, SetAudio] = useState('');

  const Playit = () => {
    audio.play();
  };
  const Stopit = () => {
    audio.pause();
  };
  useEffect(() => {
    SetAudio(new Audio('/src/assets/song.mp3'));
  }, []);
  return (
    <>
      <Carousel showArrows={true}>
        <div>
          <img src={Image1} height={200} />
        </div>
        <div>
          <img src={Image1} />
        </div>
        <div>
          <img src={Image1} />
        </div>
      </Carousel>
      <div className='container'>
        {/* <Carousel
        className='carousalBox'
        autoPlay={true}
        style={{ border: '2px solid maroon' }}>
        {Images.map((image, index) => (
          <div key={index} style={{ width: '640px' }}>
            <img
              src={image}
              className='carousalImgs'
              style={{ objectFit: 'cover', borderRadius: '15px' }}
            />
          </div>
        ))}
      </Carousel> */}
        <div className='data'>
          <p> Please join us at the </p>
          <h3>Blessing</h3>
          <p>of our Son</p>
          <h2>♡Greetik♡</h2>
          <p>(Son of Anchal and Gajendra Singh Deval) </p>
          <h4 style={{ textDecoration: 'underline' }}>Functions</h4>
          <div className='functions'>
            <h3>Katha - 9 am</h3>
            <h3>Jalwa Poojan - 4 pm</h3>
            <h3>Dinner - 6 pm onwards</h3>
          </div>
          <h3 style={{ textDecoration: 'underline' }}>
            Monday, November 21, 2022
          </h3>
          <p style={{ textDecoration: 'underline' }}>Venue</p>
          <h3>Mangalam C-181, Kirti Nagar,</h3>
          <h3> Magra Poonjla, Jodhpur</h3>
        </div>
      </div>
    </>
  );
}
